mapper.sql:
WHERE active = 'Y'

    AND name = #{name}


    AND description = #{description}


    AND event_interface = #{eventInterface}


    AND event_type = #{eventType}


    AND qif_receiver = #{qifReceiver}


    AND audit_trail_enabled = #{auditTrailEnabled}


    AND keep_message_content = #{keepMessageContent}


    AND active_accept_message = #{activeAcceptMessage}



    ORDER BY ${columnName} ${sortMethod}

WHERE active = 'Y'
AND ( lower(name) LIKE #{searchKeyword} OR lower(event_interface) LIKE #{searchKeyword} OR lower(event_type) LIKE #{searchKeyword} OR lower(description) LIKE #{searchKeyword} OR lower(qif_receiver) LIKE #{searchKeyword} OR lower(audit_trail_enabled) LIKE #{searchKeyword} OR lower(keep_message_content) LIKE #{searchKeyword} OR lower(active_accept_message) LIKE #{searchKeyword} )
ORDER BY ${columnName} ${sortMethod}

mapper.update:
UPDATE qif_event SET
name = #{name}, description = #{description}, event_interface = #{eventInterface}, event_type = #{eventType}, qif_receiver = #{qifReceiver}, audit_trail_enabled = #{auditTrailEnabled}, keep_message_content = #{keepMessageContent}, active_accept_message = #{activeAcceptMessage},
active = #{active, typeHandler=com.irwin13.winwork.mybatis.typehandler.YesNoBooleanTypeHandler}, last_update_by = #{lastUpdateBy}, last_update_date = #{lastUpdateDate} WHERE id = #{id}
